package corejava2_1.datatypes;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Explicit Type Casting
		long l = 1000;
		
		int i = (byte)l;
		int ik = (int)l;
//		byte b = (int)l;
		byte bk = (byte)l; 
		
		System.out.println("Value : " + bk +(" ") + ik + (" ") + i);
				

	}

}
