package corejava2_2.operators;

public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int Op1 = 10;
		int Op2 = 20;
		
		System.out.println(Op1 > Op2);
		System.out.println(Op1 < Op2);
		System.out.println(Op1 == Op2);
		System.out.println(Op1 != Op2);
		System.out.println(Op1 >= Op2);
		System.out.println(Op1 <= Op2);
//		always returns boolean values
		
	}

}
